    /**
     * Validates the given client data.
     * Checks if the email and phone number provided in the client object meet the required criteria.
     *
     * @param client The client object to validate.
     * @throws IllegalArgumentException if the email or phone number is invalid.
     */
    @Override
    public void validate(Client client) {
        Pattern pattern = Pattern.compile(EMAIL_PATTERN);
        if(!pattern.matcher(client.getEmail()).matches()){
            AlertUtils.showAlert("The email is not valid! Example: smith@example.com");
            throw new IllegalArgumentException("Invalid email");
        }

        pattern = Pattern.compile("^\\d{10}$");
        if(!pattern.matcher(client.getPhone()).matches()) {
            AlertUtils.showAlert("The phone number is not valid! It should contain only numbers and have exactly 10 characters.");
            throw new IllegalArgumentException("Invalid phone number");
        }
    }
